import { Image, Input } from "antd";
import { Button, Upload } from "antd";
import { PlusCircleOutlined } from "@ant-design/icons";
import { useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate } from "react-router-dom";
import { getText } from "../../../../utils/util";
import "./index.css";
import { imageConfig } from "../../../../config/config";
import { Toast } from "antd-mobile";

export default function CenterPage({ sendMsg, listData }) {
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const messagesRef = useRef(null);
  const { t: translate } = useTranslation();
  const [length, setLength] = useState(0);
  //上传
  const handleChange = (info) => {
    if (info.file.status === "done") {
      if (info.file.response && info.file.response.ok) {
        Toast.show({
          content: `${info.file.name} ${translate(getText("上傳成功"))}`,
        });
        const data = info.file.response;
        sendMsg(`image:${data.data}`);
      } else {
        Toast.show({ content: translate(getText(`上传失败`)) });
      }
    } else if (info.file.status === "error") {
      Toast.show({ content: translate(getText(`上传失败`)) });
    }
  };
  //自动滚动到底部
  const scrollDiv = () => {
    if (messagesRef.current) {
      // 使用 scrollHeight 属性来获取整个滚动内容的高度
      messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
    }
  };
  //加载节点
  const getNodes = () => {
    const nodes = [];
    for (const data of listData) {
      const node =
        data.type === 2 ? (
          <div className="servererCenter-12">
            <div className="servererCenter-13">
              {data.content.startsWith("image:") ? (
                <div
                  style={{
                    float: "right",
                    padding: "5px",
                    backgroundColor: "#f5f5f5",
                  }}
                >
                  <Image
                    width="100px"
                    height="100px"
                    src={
                      imageConfig.baseImageUrl +
                      data.content.replace("image:", "")
                    }
                    fallback="data:image/png;base64,"
                  />
                </div>
              ) : (
                <div
                  style={{
                    backgroundColor: "#f5f5f5",
                    padding: "2px 10px",
                    borderRadius: "5px",
                    whiteSpace: "pre-line",
                  }}
                >
                  {data.content}
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="servererCenter-15">
            <div className="servererCenter-16">
              {data.content.startsWith("image:") ? (
                <div
                  style={{
                    float: "left",
                    padding: "5px",
                    backgroundColor: "#f5f5f5",
                  }}
                >
                  <Image
                    width="100px"
                    height="100px"
                    src={
                      imageConfig.baseImageUrl +
                      data.content.replace("image:", "")
                    }
                    fallback="data:image/png;base64,"
                  />
                </div>
              ) : (
                <div
                  style={{
                    backgroundColor: "#f5f5f5",
                    padding: "2px 10px",
                    borderRadius: "5px",
                    whiteSpace: "pre-line",
                  }}
                >
                  {data.content}
                </div>
              )}
            </div>
          </div>
        );
      nodes.push(node);
    }
    //滚动
    if (length < listData.length) {
      scrollDiv();
      setLength(listData.length);
    }
    return nodes;
  };

  return (
    <div className="servererCenter-1">
      <div className="servererCenter-2">
        <div className="servererCenter-3">
          <div className="css-vurnku">
            <div className="css-gnqbje">
              <div className="servererCenter-8">
                <div className="servererCenter-9">
                  <div className="servererCenter-10">
                    {translate(getText("请直接提交您的问题,等待客服回复即可"))}
                  </div>
                </div>
                <div
                  id="msgbox"
                  ref={messagesRef}
                  className="servererCenter-11"
                >
                  {getNodes()}
                  <div
                    style={{
                      height: "30px",
                    }}
                  ></div>
                </div>
                <div className="servererCenter-30">
                  <div className="servererCenter-31">
                    <Input.TextArea
                      rows={1}
                      placeholder={translate(getText("请输入信息内容"))}
                      className="servererCenter-32"
                      id="content"
                      name="content"
                      value={content}
                      onChange={(e) => {
                        setContent(e.target.value);
                      }}
                    />
                    <Upload
                      name="file"
                      accept="image/*"
                      showUploadList={false}
                      action={imageConfig.uploadUrl}
                      onChange={handleChange}
                    >
                      <PlusCircleOutlined className="upIcon" />
                    </Upload>
                    <Button
                      className="servererCenter-33"
                      type="primary"
                      onClick={() => {
                        setContent("");
                        sendMsg(content);
                      }}
                    >
                      {translate(getText("發送"))}
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
